<?php

namespace Filisko\PDOplus\Tracy;

use Filisko\PDOplus\PDO;
use Tracy\IBarPanel;

/**
 * @codeCoverageIgnore
 */
class BarPanel implements IBarPanel
{
    /**
     * Base64 icon for Tracy panel.
     * @var string
     * @see http://www.flaticon.com/free-icon/database_51319
     * @author Freepik.com
     * @license http://file000.flaticon.com/downloads/license/license.pdf
     */
    public $icon = 'data:image/svg+xml;utf8;base64,';

    /**
     * Title
     */
    public string $title = 'PDO logger';

    /**
     * Title HTML attributes
     */
    public string $title_attributes = 'style="font-size:1.6em"';

    /**
     * Time table cell HTML attributes
     */
    public string $time_attributes = 'style="font-weight:bold;color:#333;font-family:Courier New;font-size:1.1em"';

    /**
     * Query table cell HTML attributes
     */
    public string $query_attributes = '';

    private PDO $pdo;

    public function __construct(PDO $pdo)
    {
        $this->pdo = $pdo;
    }

    /**
     * Get total queries execution time
     */
    protected function getTotalTime(): string
    {
        return (string) round(array_sum(array_column($this->pdo->getLog(), 'time')), 4);
    }

    /**
     * Renders HTML code for custom tab.
     */
    public function getTab(): string
    {
        $html = '<img src="'.$this->icon.'" alt="PDO queries logger" /> ';
        $queries = count($this->pdo->getLog());
        if ($queries == 0) {
            $html .= 'no queries!';
            return $html;
        } elseif ($queries == 1) {
            $html .= '1 query';
        } else {
            $html .= $queries . ' queries';
        }
        $html .= ' / '.$this->getTotalTime().' ms';
        return $html;
    }

    /**
     * Renders HTML code for custom panel.
     * @return string
     */
    public function getPanel(): string
    {
        if (class_exists('\SqlFormatter')) {
            \SqlFormatter::$pre_attributes = 'style="color: black;"';
        }
        $queries = $this->pdo->getLog();
        $html = '<h1 '.$this->title_attributes.'>'.$this->title.'</h1>';
        $html .= '<div class="tracy-inner tracy-InfoPanel">';
        if (count($queries) > 0) {
            $html .= '<table class="tracy-sortable">';
            $html .= '<tr>';
            $html .= '<th>Time(ms)</td>';
            $html .= '<th>Statement</td>';
            $html .= '</tr>';
            foreach ($queries as $query) {
                $html .= '<tr>';
                $html .= '<td><span '.$this->time_attributes.'>'.round($query['time'], 4).'</span></td>';
                if (class_exists('\SqlFormatter')) {
                    $html .= '<td>'.\SqlFormatter::highlight($query['statement']).'</td>';
                } else {
                    $html .= '<td '.$this->query_attributes.'>'.$query['statement'].'</td>';
                }
                $html .= '</tr>';
            }
            $html .= '</table>';
        } else {
            $html .= '<p style="font-size:1.2em;font-weigt:bold;padding:10px">No queries were executed!</p>';
        }
        $html .= '</div>';

        return $html;
    }
}
